var esdb = require('easy_db');
var target = 'dev';

var argv = process.argv;
var kvs = {};
for(var key in argv)
{
    if(key > 1)
    {
        var kv = argv[key].split("=");
        kvs[kv[0]] = kv[1];
    }
}
if(kvs.target)
{
    target = kvs.target;
}
//runtime target
exports.target = target;

//config db basic type
var dbs = {
    'devMg':{
        config:{'url':'mongodb://127.0.0.1:27017/football'},
        type:esdb.prop.dbType.mongodb
    },
    'devMysql':{
        config:{
            'host':'localhost',
            'user':'root',
            'password':'123456',
            'port':3306,
            'database':'football'
        },
        type:esdb.prop.dbType.mysql,
        dateToLong: true
    }
}

if(target == 'dev')
{
    exports.mg = dbs.devMg;
    exports.main = dbs.devMysql;
}

//if user hasn't operation in half a hour, the key will be expired.
exports.loginExpiredSeconds = 30*60;



