var Cons = function(){
    var self = this;
    self.info = [
        {id:0, code:'NONE', des:"无"},
        {id:1, code:'DB', des:"数据库"},
        {id:2, code:'CACHE', des:"缓存"}
    ];
    self.infoArray = {};
    self.init();
};

Cons.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

Cons.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new Cons();

