var async = require('async');

var esut = require('easy_util');
var digestUtil = esut.digestUtil;
var log = esut.log;
var pageUtil = esut.pageUtil;
var dateUtil = esut.dateUtil;

var dao = require('dao');
var dc = dao.dc;

var config = require('config');
var prop = config.prop;

var MatchPageControl = function(){};

MatchPageControl.prototype.handle = function(headNode, bodyNode, cb)
{
    console.log(bodyNode);
    var self = this;
    var cmd = headNode.cmd;
    self[cmd[1]](headNode, bodyNode, cb);
}

MatchPageControl.prototype.add = function(headNode, bodyNode, cb)
{
    var self = this;
    var backBodyNode = {title:"add season"};
    backBodyNode.curTime = dateUtil.getCurTime();
    cb(null, backBodyNode);
};

MatchPageControl.prototype.list = function(headNode, bodyNode, cb)
{
    var self = this;
    var backBodyNode = {title:"view all matches"};
    pageUtil.parse(bodyNode, backBodyNode);
    var table = dc.main.get("jmatch");
    var cursor = table.find(backBodyNode.cond, {}).sort(backBodyNode.sort).limit(backBodyNode.skip, backBodyNode.limit);
    cursor.dateToString();
    cursor.toArray(function(err, data){
        backBodyNode.rst = data;
        backBodyNode.count = cursor.count(function(err, count){
            backBodyNode.count = count;
            cb(null, backBodyNode);
        })
    })
}

module.exports = new MatchPageControl();