#include "NumType.h"

NumType::NumType(char *pNum, int len)
{
    this->charLen = 0;
    this->pChar = (char *)malloc(sizeof(char)*4);
    this->pCharCount = (int *)malloc(sizeof(int)*4);

    for(int i = 0; i < len; i++)
    {
        char c = pNum[i];
        if(c != ',' && c > '0' && c < '9')
        {
            this->put(c);
        }
    }

    if(this->charLen == 4)
    {
        this->type = 24;
    }
    else if(this->charLen == 3)
    {
        this->type = 12;
    }
    else if(this->charLen == 2 && this->pCharCount[0] == 2)
    {
        this->type = 6;
    }
    else if(this->charLen == 2)
    {
        this->type = 4;
    }
    else
    {
        this->type = -1;
    }
}

int NumType::getCharLen()
{
    return this->charLen;
}

int NumType::getType()
{
    return this->type;
}

void NumType::put(char c)
{
    if(this->charLen == 0)
    {
        this->pCharCount[0] = 1;
        this->pChar[0] = c;
        this->charLen++;
    }
    else
    {
        int hitIndex = -1;
        for(int i = 0; i < this->charLen; i++)
        {
            char tmpChar = this->pChar[i];
            if(tmpChar == c)
            {
                hitIndex = i;
                break;
            }
        }
        if(hitIndex > -1)
        {
            this->pCharCount[hitIndex] += 1;
        }
        else
        {
            this->pCharCount[this->charLen] = 1;
            this->pChar[this->charLen] = c;
            this->charLen++;
        }
    }
}

NumType::~NumType()
{
    delete[] this->pChar;
    delete[] this->pCharCount;
}